package com.example.root.kamus3.helper;import android.content.Context;import android.content.SharedPreferences;import android.preference.PreferenceManager;import com.example.root.kamus3.R;public class MyPreferences {    SharedPreferences preferences;    Context context;    public MyPreferences(Context context) {        preferences = PreferenceManager.getDefaultSharedPreferences(context);        this.context= context;    }    public void setFirstRun(Boolean input){        SharedPreferences.Editor editor = preferences.edit();        String key = context.getResources().getString(R.string.firstrun);        editor.putBoolean(key, input);        editor.commit();    }    public boolean getFirstRun(){        String key = context.getResources().getString(R.string.firstrun);        return preferences.getBoolean(key, true);    }}